import React, {useContext, useEffect} from "react";
import PropTypes from 'prop-types';
import TaskContext from '../../context/task/taskContext';

const Summary = ({valueMax, currentValue}) => {
  const taskContext = useContext(TaskContext);
  const {
        loadProjectSummary, 
        percentageConcluded, 
        loading
      } = taskContext;
 
  useEffect(() => {
    loadProjectSummary();
    // eslint-disable-next-line 
  },[loading]);

  return (
    <div className='projectSummary'>
      <span>Project summary: </span>
      <div className='progress'>
        <div className='progress-bar progress-bar-striped' style={{width: percentageConcluded+'%'}} role='progressbar' aria-valuemin='0' aria-valuemax='100' aria-valuenow={percentageConcluded}>{percentageConcluded+'%'}</div>
      </div>
    </div>
  );
};

Summary.defaultProps = {
  valueMax: 100,
  currentValue: 35,
};

Summary.propTypes = {
  valueMax: PropTypes.number.isRequired,
  currentValue: PropTypes.number.isRequired
};

export default Summary;
