import {
  SHOW_DIALOG  
} from '../types';

// eslint-disable-next-line import/no-anonymous-default-export
export default (state, action) => {
  switch(action.type){   
    case SHOW_DIALOG: 
      return {
        ...state,
        show_dialog: !state.show_dialog        
      };    
    default:       
      return state;
  }
}
