import React, {useContext, useEffect} from "react";
import TaskContext from '../../context/task/taskContext';
import State from "./State"
import TaskDeleteButton from "./TaskDeleteButton";
import TaskCompleteButton from "./TaskCompleteButton";


const TaskGrid = () => {
  const taskContext = useContext(TaskContext);
    
  useEffect(() => {
    taskContext.getTasks();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  },[]);

  return (
    <>
      <table key='taskTable'  className='taskGrid table table-striped table-bordered' >
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Estimate</th>
            <th>State</th>
            <th></th>
          </tr>
        </thead>     
        <tbody>          
            {taskContext.tasks.map(data => (            
              <tr key={data.id}>                
                <td key={data.name}>{data.name}</td>
                <td key={data.description}>{data.description}</td>
                <td key={data.estimate}>{data.estimate + ' hrs'}</td>
                <td key={data.state} ><State state={taskContext.getStatus(data)}/></td> 
                <td key ='action' className='gridActionColumn'>
                  <div style='border: 1px solid black'>
                    <div className='col-md-6' style='border: 1px solid blue'>
                      <TaskDeleteButton index={data.id} />                  
                    </div>
                    <div className='col-md-6' style='border: 1px solid red'> 
                      <TaskCompleteButton index={data.id} status={taskContext.getStatus(data)} />
                    </div>
                  </div>                  
                </td> 
              </tr>            
            ))}                    
        </tbody> 
      </table>
    </>
  );
};

export default TaskGrid;
