import React, {useContext, useEffect} from "react";
import TaskContext from '../../context/task/taskContext';
import Status from "./Status"
import DeleteButton from "./DeleteButton";
import CompleteButton from "./CompleteButton";
import StartButton from './StartButton'
import { Container, Row, Col } from "react-bootstrap";


const TaskGrid = () => {
  const taskContext = useContext(TaskContext);
    
  useEffect(() => {
    taskContext.getTasks();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  },[]);

  return (
    <>
      <table key='taskTable'  className='taskGrid table table-striped table-bordered' >
        <thead>
          <tr>
            <th className='ten'>Name</th>
            <th className='forty'>Description</th>
            <th className='ten'>Estimate</th>
            <th className='twenty'>State</th>
            <th className='twenty'></th>
          </tr>
        </thead>     
        <tbody>          
            {taskContext.tasks.map(data => (            
              <tr key={data.id}>                
                <td className='ten' key={data.name}>{data.name}</td>
                <td className='forty' key={data.description}>{data.description}</td>
                <td className='ten' key={data.estimate}>{data.estimate + ' hrs'}</td>
                <td className='twenty' key={data.state} ><Status state={taskContext.getStatus(data)}/></td> 
                <td className='twenty' key ='action' className='gridActionColumn'>
                  
                    <Row>
                      <Col>
                        <DeleteButton index={data.id}  />                  
                      </Col>
                      <Col>
                        <StartButton index={data.id} status={taskContext.getStatus(data)} />
                      </Col>
                      <Col>
                        <CompleteButton index={data.id} status={taskContext.getStatus(data)} />                    
                      </Col>
                    </Row>
                  
                </td> 
              </tr>            
            ))}                    
        </tbody> 
      </table>
    </>
  );
};

export default TaskGrid;
