import React from "react";
import tasks from "../../data.json"

const calculateEstimate = function(beginDate, endDate){
  var t2 = endDate.getTime();
  var t1 = beginDate.getTime();

  return parseInt((t2-t1)/(24*3600*1000));
};

const TaskGrid = () => {
  return (
    <table className='taskGrid' >
      <thead>
        <tr>
          <th></th>
          <th>Name</th>
          <th>Description</th>
          <th>Estimate</th>
          <th>State</th>
        </tr>
      </thead>     
      <tbody>
        {tasks.map(data => (
          <tr>
            <td><input type='checkbox'></input></td>
            <td>{data.name}</td>
            <td>{data.description}</td>
            <td>{calculateEstimate(data.beginDate, data.estimateEndDate)}</td>
            <td>{data.beginDate}</td> 
          </tr>
        ))}
        
      </tbody> 
    </table>
  );
};

export default TaskGrid;
