import {
  GET_TASKS,
  SET_LOADING,
  ADD_TASK,
  DELETE_TASK,
  CONCLUDE_TASK
} from '../types';

export default (state, action) => {
  switch(action.type){
    case GET_TASKS:
      return {
        ...state,
        tasks: action.payload,
        loading: false
      };
    case DELETE_TASK:
      return {
        ...state,
        tasks: action.payload,
        loading: false
      };
    case SET_LOADING:
      return{
        ...state,
        loading: true
      };
    default: 
      return state;
  }
}
