import React, {useReducer} from 'react';
import TaskContext from './taskContext';
import TaskReducer from './taskReducer';
import data from "../../data.json";

import {
  GET_TASKS,
  SET_LOADING,
  ADD_TASK,
  DELETE_TASK,
  CONCLUDE_TASK
} from '../types';

const TaskState = props => {
  const initialState = {
    tasks: [],
    loading: false
  };

  const [state, dispatch] = useReducer(TaskReducer, initialState);

  //Get tasks
  const getTasks = () => {
    setLoading();

    //Here we can call axios getting from server REST API. I'm using local data just to present 
    const tasks = data;

    dispatch({
      type: GET_TASKS,
      payload: tasks
    });
  }

  //Get Task status
  const getStatus = function(record){

    if (record.endDate != null){
      return {value: 'Completed', colorClass: 'bg-green'};
    }
  
    if (new Date(record.beginDate) > new Date()){
      return {value: 'Planned', colorClass: 'bg-red'};
    }
  
    return {value: 'In Process', colorClass: 'bg-yellow'};
  
  };

  //Add task
  const addTask = (newTask) => {
    setLoading();

    if (state.tasks && newTask) {
      state.tasks = state.tasks.concat(newTask);
    }

    dispatch({
      type: ADD_TASK,
      payload: state.tasks
    })

  }

  //Delete task
  const deleteTask = (index) => {
    setLoading();

    if (state.tasks) {
      state.tasks = state.tasks.filter(t => t.id !== index );
    }

    dispatch({
      type: DELETE_TASK,
      payload: state.tasks
    })

  }

  //Conclude task
  const concludeTask = (index) => {
    setLoading();
    
    console.log('Index: '+ index);
    console.log (state.tasks);

    if (state.tasks) {
      const ind = state.tasks.findIndex(t => t.id === index );
      state.tasks[ind].endDate = new Date();
    }
    
    console.log (state.tasks);

    dispatch({
      type: CONCLUDE_TASK,
      payload: state.tasks
    })

  }


  //Set loading
  const setLoading = () => dispatch({type: SET_LOADING});

  return <TaskContext.Provider
    value={{
      tasks: state.tasks,
      loading: state.loading,
      getTasks,
      getStatus,
      deleteTask,
      concludeTask,
      addTask
    }}
  >
    {props.children}
  </TaskContext.Provider>

};

export default TaskState;
