import React, {useContext} from "react";
import PropTypes from "prop-types";
import img from '../../images/icons8-delete-64.png';
import ConfirmationDialog from "../modals/ConfirmationDialog";
import TaskContext from "../../context/task/taskContext";
import DialogContext from '../../context/dialog/dialogContext';
import DialogState from '../../context/dialog/DialogState';

const TaskDeleteButton = ({index}) => {
  const taskContext = useContext(TaskContext); 
  const dialogContext = useContext(DialogContext);

  /*const [showConfirmationDialog, setShowConfirmationDialog] = useState(false);

  const showDialog = () => {
    setShowConfirmationDialog(show => !show);
  }*/

  return(
    <>
      <DialogState>
        <img className='taskGridCommands' src={img} value={index} onClick={dialogContext.setShowDialog} 
            alt="" data-toggle="tooltip" data-placement="top" title="Delete task by clicking here"></img>
        <ConfirmationDialog title="Warning" 
          message="Do you really want to delete this task?" 
          positiveBtn='Yes' negativeBtn='No' 
          positiveResponse={taskContext.deleteTask(index) } 
          negativeResponse={dialogContext.setShowDialog} />      
      </DialogState>
    </>
  );
}

TaskDeleteButton.defaultProps = {
  deleteButtonImg: "../../images/icons8-delete-64",
  index: -1
};

TaskDeleteButton.propTypes = {
  deleteButtonImg: PropTypes.string.isRequired,
  index: PropTypes.number.isRequired
};

export default TaskDeleteButton;
