import {
  GET_TASKS,
  SET_LOADING,
  ADD_TASK,
  DELETE_TASK,
  CONCLUDE_TASK
} from '../types';

// eslint-disable-next-line import/no-anonymous-default-export
export default (state, action) => {
  switch(action.type){
    case (GET_TASKS || DELETE_TASK || ADD_TASK || CONCLUDE_TASK):
      console.log('****Entrou***')
      return {
        ...state,
        tasks: action.payload,
        loading: false
      };    
    case SET_LOADING:
      console.log('***Loading****')
      return{
        ...state,
        loading: true
      };
    default: 

      return state;
  }
}
