import React from "react";
import PropTypes from 'prop-types';

const Summary = ({valueMax, currentValue}) => {
  return (
    <div className='projectSummary'>
      <span>Project summary: </span>
      <div className='progress'>
        <div className='progress-bar progress-bar-striped' style={{width: currentValue+'%'}} role='progressbar' aria-valuemin='0' aria-valuemax={valueMax} aria-valuenow={currentValue}></div>

      </div>
    </div>
  );
};

Summary.defaultProps = {
  valueMax: 100,
  currentValue: 35,
};

Summary.propTypes = {
  valueMax: PropTypes.number.isRequired,
  currentValue: PropTypes.number.isRequired
};

export default Summary;
