import React, {useState, useContext} from "react";
import PropTypes from 'prop-types';
import AddTaskForm from './AddTaskForm';
import {Button, Modal} from 'react-bootstrap';
import TaskContext from "../../context/task/taskContext";

const AddButton = ({title}) => {
  const taskContext = useContext(TaskContext);
  const [showModal, setShowModal] = useState(false);

  const openModal = () => {
    setShowModal(prev => !prev);
  }

  const {addTask, newTask} = taskContext;

  const addNewTask = () => {
    if (newTask.name == '' || newTask.description == '' || newTask.estimate == 0){
      return null;
    }
    addTask();
    openModal();
  }

  return (
    <>
      <Button onClick={openModal} className='btn btn-primary taskAddButton'>{title}</Button>
      <Modal show={showModal}>
        <Modal.Header>
          Add a new task
        </Modal.Header>
        <Modal.Body>
          <form className='needs-validation' onSubmit={addNewTask} >
            <AddTaskForm></AddTaskForm>  
            <div className='formButtonsContainer'>
              <Button variant='secondary' onClick={openModal} className='formButtons'>
                Close
              </Button>                 
              <input type='submit' value='Save' className='btn btn-primary formButtons'/>                 
            </div>
          </form>
        </Modal.Body>
        {/* <Modal.Footer>

          <Button type='submit' variant='primary' onClick={addNewTask}>
            Save
          </Button>
          <Button variant='secondary' onClick={openModal}>
            Close
          </Button>
        </Modal.Footer> */}
      </Modal>      
    </>
  );
};

AddButton.defaultProps = {
  title: "Add Task"
};

AddButton.propTypes = {
  title: PropTypes.string.isRequired
}

export default AddButton;
