import {
  SHOW_ALERT,
  CLOSE_ALERT
} from '../types';

// eslint-disable-next-line import/no-anonymous-default-export
export default (state, action) => {
  switch(action.type){   
    case SHOW_ALERT:  
      console.log('Show Alert: '+ state.alertVariant);     
      return {
        ...state,
        showAlert: !state.showAlert,
        message: state.message,
        alertVariant: state.alertVariant,
        index: state.index + 1
      };    
    case CLOSE_ALERT:   
    console.log('Close Alert: '+ state.showAlert);         
      return {
        ...state,
        showAlert: false        
      };    
    default:       
      return state;
  }
}
