import React, {useState, useContext} from 'react'
import hours from '../../hours.json'
import TaskContext from '../../context/task/taskContext';

const AddTaskForm = () => {
  const taskContext = useContext(TaskContext);

  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [estimate, setEstimate] = useState('');
  
  const {newUser} =taskContext;

  return (
    <form className='needs-validation' noValidate>
      <div className='form-group row'>
        <label htmlFor='taskName' className='col-sm-3 col-form-label'>Task Name: </label>
        <div className='col-sm-9'>
          <input 
            type='text' 
            id='taskName'
            name='taskName' 
            className='form-control'             
            value={newUser.name}
            onChange={(e) => newUser.name = e.target.value}
            required></input>
        </div>
      </div>
      <div className='form-group row'>
        <label htmlFor='taskDescription' className='col-sm-3 col-form-label'>Description: </label>
        <div className='col-sm-9'>
          <textarea id='taskDescription' rows='3' className='form-control' required></textarea>
        </div>
      </div>     
      <div className='form-group row'>
        <label htmlFor='taskEstimate' className='col-sm-3 col-form-label'>Estimate: </label>
        <div className='col-sm-9'>
          <select className="form-control" id="exampleFormControlSelect1">
            {hours.map(hour => (
              <option key={hour}>{hour}</option>  
            ))}            
          </select>
        </div>
      </div>
    </form> 
  );
}

export default AddTaskForm;
