import React, {useState} from "react";
import PropTypes from "prop-types";
import img from '../../images/icons8-delete-64.png';
import {Button, Modal} from 'react-bootstrap';
import ConfirmationDialog from "../modals/ConfirmationDialog";

const TaskDeleteButton = ({index}) => {
  const [showConfirmationDialog, setShowConfirmationDialog] = useState(false);

  const showDialog = () => {
    setShowConfirmationDialog(show => !show);
  }

  return(
    <>
      <img className='taskGridCommands' src={img} value={index} onClick={showDialog} alt="" data-toggle="tooltip" data-placement="top" title="Delete task by clicking here"></img>
      <Modal show={showConfirmationDialog}>    
          <Modal.Header>
            Warning!
          </Modal.Header>
          <Modal.Body>
            Do you really want to delete this task?           
          </Modal.Body>
          <Modal.Footer>
            <Button variant='primary' onClick={showDialog}>
              No
            </Button>
            <Button variant='secondary' onClick={showDialog}>
              Yes
            </Button>
          </Modal.Footer>
        </Modal>      
    </>
  );
}

TaskDeleteButton.defaultProps = {
  deleteButtonImg: "../../images/icons8-delete-64",
  index: -1
};

TaskDeleteButton.propTypes = {
  deleteButtonImg: PropTypes.string.isRequired,
  index: PropTypes.number.isRequired
};

export default TaskDeleteButton;
