import {
  GET_TASKS,
  SET_LOADING,
  ADD_TASK,
  DELETE_TASK,
  CONCLUDE_TASK
} from '../types';

export default (state, action) => {
  switch(action.type){
    
    case SET_LOADING:
      return{
        ...state,
        loading: true
      }
    default: action
  }
}
