import React, {useState, useContext} from 'react'
import hours from '../../hours.json'
import TaskContext from '../../context/task/taskContext';

const AddTaskForm = () => {
  const taskContext = useContext(TaskContext);

  const {newTask, checkFormValidity} = taskContext;

  const onChange = (field, e) => {
    console.log(field);
    switch (field){
      case 'name':
        newTask.name = e.target.value;
      case 'description':
          newTask.description = e.target.value;
      case 'estimate':        
        newTask.estimate = e.target.value;
      
    }
    
    checkFormValidity();
  }

  return (    
    <>
      <div className='form-group row'>
        <label htmlFor='taskName' className='col-sm-3 col-form-label'>Task Name: </label>
        <div className='col-sm-9'>
          <input 
            type='text' 
            id='taskName'
            name='taskName' 
            className='form-control'                         
            onChange={(e) => onChange('name', e) }
            required></input>
        </div>
      </div>
      <div className='form-group row'>
        <label htmlFor='taskDescription' className='col-sm-3 col-form-label'>Description: </label>
        <div className='col-sm-9'>
          <textarea 
            id='taskDescription' 
            name='taskDescription'
            rows='3' 
            className='form-control'   
            onChange={(e) => onChange(newTask.description, e) }                           
            required></textarea>
        </div>
      </div>     
      <div className='form-group row'>
        <label htmlFor='taskEstimate' className='col-sm-3 col-form-label'>Estimate: </label>
        <div className='col-sm-9'>
          <select required
            className="form-control" 
            id="taskEstimate"
            name="taskEstimate"                   
            onChange={(e) => onChange(newTask.estimate, e) }            
            >
            <option selected disabled>Select </option>
            {hours.map(hour => (
              <option key={hour} value={hour}>{hour}</option>  
            ))}            
          </select>
        </div>
      </div>
    </>
  );
}

export default AddTaskForm;
