import React, {useState} from "react";
import PropTypes from 'prop-types';
import AddTaskModal from './AddTaskModal';
import {Button, Modal} from 'react-bootstrap';

const TaskAddButton = ({title}) => {
  const [showModal, setShowModal] = useState(false);

  const openModal = () => {
    setShowModal(prev => !prev);
  }

  return (
    <>
      <button onClick={openModal} className='btn btn-primary taskAddButton'>{title}</button>
      <Modal show={showModal}>
        <Modal.Body>
          Hi
        </Modal.Body>
        <Modal.Footer>
          <Button>
            Save
          </Button>
          <Button>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
      <AddTaskModal showModal={showModal} setShowModal={setShowModal} />
    </>
  );
};

TaskAddButton.defaultProps = {
  title: "Add Task"
};

TaskAddButton.propTypes = {
  title: PropTypes.string.isRequired
}

export default TaskAddButton;
