import React, {useState, useContext} from 'react';
import TaskContext from '../../context/task/taskContext';
import AddTaskForm from './AddTaskForm';
import {Button, Modal} from 'react-bootstrap';
import DialogContext from '../../context/dialog/dialogContext';

const AddTaskModal = () => {
  const taskContext = useContext(TaskContext);
  const dialogContext = useContext(DialogContext);

  const {show_dialog, openModal} = dialogContext;

  const {addTask, checkFormValidity} = taskContext;

  const addNewTask = () => {
    /* Check form validity before saving */
    if (checkFormValidity()){
      addTask();
      openModal();
    }
  };

  return (
    <>
    <span>Test: {show_dialog}</span>
    <Modal show={show_dialog}>
      <Modal.Header>
        Add a new task
      </Modal.Header>
      <Modal.Body>
        <form id='addTaskForm' className='needs-validation' >
          <AddTaskForm></AddTaskForm>                    
        </form>
      </Modal.Body>
      <Modal.Footer>

        <Button variant='primary' onClick={addNewTask}>
          Save
        </Button>
        <Button variant='secondary' onClick={openModal}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>      
    </>
  );
};

export default AddTaskModal;
