import React, {useReducer} from 'react';
import TaskContext from './taskContext';
import TaskReducer from './taskReducer';
import data from "../../data.json";

import {
  GET_TASKS,
  SET_LOADING,
  ADD_TASK,
  DELETE_TASK,
  CONCLUDE_TASK,
  LOAD_SUMMARY
} from '../types';

const TaskState = props => {
  const initialState = {
    tasks: [],
    newTask: {
      id: null,
      name: '',
      description: '',
      startDate: null,
      estimate: 0,
      endDate: null
    },
    percentageConcluded: 0,
    loading: false
  };

  const [state, dispatch] = useReducer(TaskReducer, initialState);

  //Get tasks
  const getTasks = () => {
    setLoading();
    
    //Here we can call axios getting from server REST API. I'm using local data just to present 
    const tasks = data;
    
    dispatch({
      type: GET_TASKS,
      payload: tasks   
    });        
  }

  //Get Task status
  const getStatus = function(record){

    if (record.endDate != null){
      return {value: 'Completed', colorClass: 'bg-green'};
    }
  
    if (record.startDate == null){
      return {value: 'Planned', colorClass: 'bg-red'};
    }
  
    return {value: 'In Process', colorClass: 'bg-yellow'};
  
  };

  //Add task
  const addTask = () => {
    setLoading();        
    
    if (state.tasks && state.newTask !== {}) {
      const newArr = [...state.tasks];
      state.newTask.id = state.tasks.length + 1;
      state.tasks = newArr.concat(state.newTask);
    }
        
    dispatch({
      type: ADD_TASK,
      payload: state.tasks
    });
    
  }

  //Check form validity
  const checkFormValidity = () => {    
    if (state.newTask.name === '' || state.newTask.description === '' || state.newTask.estimate === 0){      

      var myform = document.getElementById("addTaskForm")[0];
      if (!myform.checkValidity()) {
          if (myform.reportValidity) {
              myform.reportValidity();              
          }
      }
      let inputComponent = document.getElementById('taskName');
      if(state.newTask.name === ''){               
        inputComponent.classList.add('is-invalid');
      }else{        
        inputComponent.classList.remove('is-invalid');
      }

      inputComponent = document.getElementById('taskDescription');
      if(state.newTask.description === ''){      
        inputComponent.classList.add('is-invalid');
      }else{
        inputComponent.classList.remove('is-invalid');
      }

      inputComponent = document.getElementById('taskEstimate');
      if(state.newTask.estimate === 0){        
        inputComponent.classList.add('is-invalid');
      }else{
        inputComponent.classList.remove('is-invalid');
      }

      return false;
    }

    let inputComponent = document.getElementById('taskName');
    inputComponent.classList.remove('is-invalid');
    
    inputComponent = document.getElementById('taskDescription');
    inputComponent.classList.remove('is-invalid');
    
    inputComponent = document.getElementById('taskEstimate');
    inputComponent.classList.remove('is-invalid');
    
    return true;
  }

  //Delete task
  const deleteTask = (index) => {
    setLoading();
    
    if (state.tasks) {
      let newArr = [...state.tasks];
      newArr = state.tasks.filter(t => t.id !== index );
      state.tasks = newArr;
    }
    
    dispatch({
      type: DELETE_TASK,
      payload: state.tasks
    });    
  }

  //Conclude task
  const concludeTask = (index) => {
    setLoading();
    
    if (state.tasks) {
      const ind = state.tasks.findIndex(t => t.id === index );
      state.tasks[ind].endDate = new Date();
    }
    
    dispatch({
      type: CONCLUDE_TASK,
      payload: state.tasks
    });
  }

  //Start task
  const startTask = (index) => {
    setLoading();

    if (state.tasks) {
      const ind = state.tasks.findIndex(t => t.id === index );
      state.tasks[ind].startDate = new Date();
    }

    dispatch({
      type: CONCLUDE_TASK,
      payload: state.tasks
    });
  }

  //Load tasks Summary
  const loadProjectSummary = () => {
    let summary = 0;
    console.log('loading summary');
    if (state.tasks && state.tasks.length > 0){
      console.log(state.tasks);
      summary = Math.round((state.tasks.filter(task => task.endDate !== null).length / state.tasks.length) * 100);
      console.log('Summary: '+summary);      
    }
      
    dispatch({
      type: LOAD_SUMMARY,
      payload: summary
    });    
  }

  //Set loading
  const setLoading = () => dispatch({type: SET_LOADING});

  return <TaskContext.Provider
    value={{
      tasks: state.tasks,
      newTask: state.newTask,
      loading: state.loading,
      percentageConcluded: state.percentageConcluded,
      getTasks,
      getStatus,
      deleteTask,
      concludeTask,
      addTask,
      startTask,
      checkFormValidity,
      loadProjectSummary
    }}
  >
    {props.children}
  </TaskContext.Provider>

};

export default TaskState;
