import React from "react";
import tasks from "../../data.json"

const getEstimate = function(beginDate, endDate){
  var t2 = endDate;
  var t1 = beginDate;

  return parseInt((t2-t1)/(24*3600*1000));
};

const getState = function(record){

  if (record.endDate == null){
    return 'Completed';
  }

  if (record.beginDate > new Date()){
    return 'Planned';
  }

  return 'In Process';

};

const TaskGrid = () => {
  return (
    <table className='taskGrid' >
      <thead>
        <tr>
          <th></th>
          <th>Name</th>
          <th>Description</th>
          <th>Estimate</th>
          <th>State</th>
        </tr>
      </thead>     
      <tbody>
        {tasks.map(data => (
          <tr>
            <td><input type='checkbox'></input></td>
            <td>{data.name}</td>
            <td>{data.description}</td>
            <td>{getEstimate(data.beginDate, data.estimateEndDate)}</td>
            <td>{getState(data)}</td> 
          </tr>
        ))}
        
      </tbody> 
    </table>
  );
};

export default TaskGrid;
